export const Default = () => {
  return (
    <div style={{ height: '50vh' }}>
      <BasedExplorer
        onItemClick={(item) => {
          alert('clicked item ' + item.id)
        }}
        fields={{
          id: { type: 'string', format: 'basedId' },
          name: { title: 'Power name', type: 'string', display: 'capitalize' },
          updatedAt: { type: 'timestamp', display: 'time-precise' },
        }}
        query={({ limit, offset, sort, language }) => ({
          $language: language,
          data: {
            $all: true,
            $list: {
              $limit: limit,
              $offset: offset,
              ...(sort && {
                $sort: {
                  $field: sort.key,
                  $order: sort.dir,
                },
              }),
              $find: {
                $traverse: 'children',
                $filter: [{ $operator: '=', $field: 'type', $value: 'todo' }],
              },
            },
          },
        })}
        totalQuery={() => ({
          total: {
            $aggregate: {
              $function: 'count',
              $traverse: 'children',
              $filter: [
                {
                  $field: 'type',
                  $operator: '=',
                  $value: 'todo',
                },
              ],
            },
          },
        })}
      />
    </div>
  )
}

